import { MoreVert } from "@material-ui/icons";
import "./post.css";
import { Users } from "../../dummyData";
import { useState } from "react";

export default function Post({ post }) {
  const [like, setLike] = useState(post.like);
  const [isLiked, setIsLiked] = useState(false);
  const handelLikeClick = () => {
    setLike(isLiked ? like - 1 : like + 1);
    setIsLiked(!isLiked);
  };
  const PF = process.env.REACT_APP_ASSETS;
  return (
    <div className="postContainer">
      <div className="postTop">
        <div className="postTopLeft">
          <img
            src={
              PF +
              Users.filter((user) => user.id === post.userId)[0].profilePicture
            }
            alt="profile"
            className="postProfileImg"
          />
          <div className="postName">
            {Users.filter((user) => user.id === post.userId)[0].username}
          </div>
          <div className="postDate">{post.date}</div>
        </div>
        <div className="postTopRight">
          <MoreVert className="postTopIcon" />
        </div>
      </div>
      <div className="postCenter">
        <div className="postDesc">{post?.desc}</div>
        <img src={PF + post.photo} alt="post" className="postImg" />
      </div>
      <div className="postBottom">
        <div className="postLeft">
          <img
            src={`${PF}/like.png`}
            alt="like"
            className="postLikeImg"
            onClick={handelLikeClick}
          />
          <img
            src={`${PF}/heart.png`}
            alt="heart"
            className="postLikeImg"
            onClick={handelLikeClick}
          />
          <span className="likeCounts">{like}</span>
        </div>
        <div className="postRight">9 comments</div>
      </div>
    </div>
  );
}
