import { createContext, useReducer } from "react";
import AuthReducer from "./AuthReducer";

const INITIAL_STATE = {
  user: {
    username: "jon",
    email: "jon@gmail.com",
    password: "$2b$10$s1rvvvubYcAWvM28vktVuOXNpbBq29llURWaqMd9wDM.Ru3DYzsBi",
    isAdmin: false,
    profilePicture: "",
    coverPicture: "",
    followings: [],

    followers: [],
    desc: "",
    city: "",
    from: "",
    createdAt: "2022-02-08T12:53:15.843+00:00",
    updatedAt: "2022-02-09T08:32:14.332+00:00",
  },
  isFetching: false,
  error: false,
};

export const AuthContext = createContext(INITIAL_STATE);

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AuthReducer, INITIAL_STATE);
  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        error: state.error,
        isFetching: state.isFetching,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
